            vm.submitPlayerFeedbackQuery = function (isNewSearch, currentTimeBoolean) {
                if (!vm.playerFeedbackQuery || !vm.playerFeedbackQuery.selectedPlatform) return;
                vm.playerFeedbackSelectedPlatform = vm.allPlatformData.filter(platform => {return vm.playerFeedbackQuery.selectedPlatform == platform._id})[0];
                vm.playerFeedbackQuery.platform = vm.playerFeedbackQuery.selectedPlatform;
                vm.loadBankCardGroupData(vm.playerFeedbackQuery.selectedPlatform);
                vm.loadMerchantGroupData(vm.playerFeedbackQuery.selectedPlatform);
                vm.loadAlipayGroupData(vm.playerFeedbackQuery.selectedPlatform);
                vm.loadWechatPayGroupData(vm.playerFeedbackQuery.selectedPlatform);
                vm.getPlatformProviderGroup(vm.playerFeedbackQuery.selectedPlatform);
                vm.rewardTabClicked(null, vm.playerFeedbackQuery.selectedPlatform);

                if (vm.ctiData.hasOnGoingMission) {
                    if (isNewSearch) {
                        vm.feedbackPlayersPara.index = 1;
                    }
                    return vm.getCallOutMissionPlayerDetail();
                }

                if (currentTimeBoolean) {
                    $('#registerEndTimePicker').data('datetimepicker').setDate(new Date(utilService.getLocalTime(new Date()).getTime() - 30 * 60 * 1000));
                }

                console.log('vm.feedback', vm.playerFeedbackQuery);
                vm.exportPlayerFilter = JSON.parse(JSON.stringify(vm.playerFeedbackQuery))
                let startTime = $('#registerStartTimePicker').data('datetimepicker').getLocalDate();
                let endTime = $('#registerEndTimePicker').data('datetimepicker').getLocalDate();

                $('#platformFeedbackSpin').show();
                vm.exportQuery = vm.getPlayerFeedbackQuery();
                console.log('vm.playerFeedbackSearchType', vm.playerFeedbackSearchType);
                if (isNewSearch) {
                    vm.feedbackPlayersPara.index = 1;
                    vm.playerFeedbackQuery.index = 0;
                }
                let isMany = {
                                limit: vm.playerFeedbackQuery.limit,
                                sortCol: vm.playerFeedbackQuery.sortCol,
                                searchType: vm.playerFeedbackSearchType
                            };
                let sendQuery = {
                    query: JSON.parse(JSON.stringify(vm.playerFeedbackQuery)),
                    index: vm.playerFeedbackQuery.index,
                    //new block
                    isMany: isMany,
                    startTime: startTime,
                    endTime: endTime
                    //new Block
                };
                let admins = [];
                if(vm.playerFeedbackQuery.departments && vm.playerFeedbackQuery.departments.length > 0 &&
                    (!vm.playerFeedbackQuery.admins || vm.playerFeedbackQuery.admins.length == 0)) {
                    vm.queryAdmins.forEach(item => {
                        admins.push(item._id);
                    });
                }
                if ( (vm.playerFeedbackQuery.admins && vm.playerFeedbackQuery.admins.length > 0) || admins.length) {
                    sendQuery.query.admins = vm.playerFeedbackQuery.admins && vm.playerFeedbackQuery.admins.length > 0 ? vm.playerFeedbackQuery.admins : admins;
                }
                console.log("getPlayerFeedbackQuery sendQuery", sendQuery)
                socketService.$socket($scope.AppSocket, 'getPlayerFeedbackQuery', sendQuery, function (data) {
                    $scope.$evalAsync(() => {
                        console.log('_getPlayerFeedbackQuery', data);
                        vm.lastSuccessBackEndQuery = data && data.data && data.data.backEndQuery;
                        if(vm.playerFeedbackSearchType === "one"){
                            console.log('_getSinglePlayerFeedbackQuery', data);
                            vm.drawSinglePlayerFeedback(data);
                        }else{
                            let playerList = data.data.data;
                            console.log(playerList);
                            // setTableData(vm.playerFeedbackTable, playerList);
                            vm.drawPlayerTable(playerList);
                            vm.playerFeedbackQuery.total = data.data.total || 0;
                            vm.playerFeedbackQuery.index = data.data.index || 0;
                            vm.playerFeedbackQuery.pageObj.init({maxCount: vm.playerFeedbackQuery.total}, isNewSearch);

                            vm.feedbackPlayersPara.total = vm.playerFeedbackQuery.total;
                            $('#platformFeedbackSpin').hide();
                            // $scope.safeApply();
                        }
                    });
                });
                vm.playerCredibilityComment = [];
            };
