            vm.editReward = function (i) {
                let isValid = true;
                let isHostResult = true;
                let isPlayerResult = true;
                let isPeriodResultValid = true;
                let isApplyTypeValid = true;
                let isReferralResult = true;
                console.log('vm.showReward', vm.showReward);

                if (vm.showReward && vm.showReward.type && vm.showReward.type.name
                    && vm.showReward.type.name == "PlayerConsumptionReturn" && vm.showReward.param
                    && vm.showReward.param.imageUrl && vm.showReward.param.imageUrl.length) {
                    vm.rewardParams.imageUrl = vm.showReward.param.imageUrl;
                }

                var curReward = {
                    name: vm.showReward.name,
                    code: vm.showReward.code,
                    type: vm.showReward.type._id,
                    needApply: vm.showReward.needApply,
                    needSettlement: vm.showReward.needSettlement,
                    canApplyFromClient: vm.showReward.canApplyFromClient,
                    showInRealServer: vm.showReward.showInRealServer,
                    settlementPeriod: vm.showReward.settlementPeriod,
                    description: vm.showReward.description,
                    platform: vm.showReward.platform,
                    param: vm.rewardParams,
                    condition: vm.rewardCondition,
                    validStartTime: vm.showReward.validStartTime || null,
                    validEndTime: vm.showReward.validEndTime || null,
                };

                if (vm.showRewardTypeData.isGrouped === true) {
                    // Set condition
                    Object.keys(vm.rewardMainCondition).forEach(e => {
                        if (vm.rewardMainCondition[e].value !== undefined) {
                            let condName = vm.rewardMainCondition[e].name;
                            let condType = vm.rewardMainCondition[e].type;
                            let condValue = vm.rewardMainCondition[e].value;

                            // Get time string in object type
                            if (condType == "date") {
                                if (condValue.find("input").val()) {
                                    condValue = condValue.data('datetimepicker').getLocalDate();
                                } else {
                                    condValue = null;
                                }
                            }

                            // Save name and code to outer level
                            if (condName == "name" || condName == "code" || condName == "canApplyFromClient" || condName == "showInRealServer" || condName == "validStartTime" || condName == "validEndTime") {
                                curReward[condName] = condValue;
                            }

                            if (condName == "interval" && condValue == "6") { // 6 == last month
                                isPeriodResultValid = false;
                            }

                            if (condName == "applyType" && condValue == "2") { // 2 == 满足条件后自动生成
                                isApplyTypeValid = false;
                            }

                            // Interval type handling
                            if (condType == "interval") {
                                if (vm.rewardMainCondition[e].hasOwnProperty("value1")) {
                                    condValue = [condValue, vm.rewardMainCondition[e].value1];

                                    if (vm.rewardMainCondition[e].hasOwnProperty("value2")) {
                                        condValue.push(vm.rewardMainCondition[e].value2);
                                    }
                                }
                            }

                            // Save reward condition
                            if ((condType == "date" && condValue) || condType != "date") {
                                curReward.condition[condName] = condValue;
                            }
                        }
                    });

                    // Set param
                    Object.keys(vm.rewardMainParam).forEach(e => {
                        curReward.param[e] = vm.rewardMainParam[e].value;
                    });

                    curReward.param.rewardParam = [];

                    if(vm.showRewardTypeData && vm.showRewardTypeData.name && vm.showRewardTypeData.name == "PlayerRandomRewardGroup"){
                        vm.repackageRandomRewardGroup();

                        // sum up all the possibilities and ensure the sum is not exceeded 100%
                        let total = 0;
                        let isExpiredInDayValid = true;
                        vm.rewardMainParamTable[0].value.forEach( row => {
                            total = total + (row.possibility || 0);
                            if (row && row.rewardType && (row.rewardType == 2 || row.rewardType == 3 || row.rewardType == 4) && !row.expiredInDay){
                                isExpiredInDayValid = false;
                            }
                        })

                        if(!isExpiredInDayValid){
                            return socketService.showErrorMessage($translate('expiredInDay has to be filled up'));
                        }

                        if($noRoundTwoDecimalPlaces(total) > 1){
                            return socketService.showErrorMessage($translate('The overall probability cannot be higher than 100%'));
                        }

                        if (curReward.condition.noRepetitiveRewardInPeriod && curReward.condition && curReward.condition.hasOwnProperty('numberParticipation') && vm.rewardMainParamTable[0].value && vm.rewardMainParamTable[0].value.length < curReward.condition.numberParticipation){
                            return socketService.showErrorMessage($translate("The number of chances is larger than the available reward, please untick the 'No Repetitive Reward In Period'"));
                        }
                    }

                    // Set param table
                    Object.keys(vm.rewardMainParamTable).forEach((e, idx) => {
                        if (vm.showRewardTypeData && vm.showRewardTypeData.name && vm.showRewardTypeData.name == 'PlayerConsumptionSlipRewardGroup'
                            && vm.rewardMainParamTable[e] && vm.rewardMainParamTable[e].value && vm.rewardMainParamTable[e].value.length > 0) {
                            vm.rewardMainParamTable[e].value.forEach(el => {
                                if (Object.keys(el) && Object.keys(el).length > 0 && (!el.minConsumptionAmount || !el.rewardAmount)) {
                                    isValid = false;
                                }
                            })
                        }

                        if (vm.showRewardTypeData && vm.showRewardTypeData.name && vm.showRewardTypeData.name === 'ReferralRewardGroup'
                            && vm.rewardCondition && vm.rewardCondition.referralRewardMode && vm.rewardCondition.referralRewardMode === '2'
                            && vm.rewardCondition.isDynamicRewardTopUpAmount
                            && vm.rewardMainParamTable[e] && vm.rewardMainParamTable[e].value && vm.rewardMainParamTable[e].value.length > 0) {
                            vm.rewardMainParamTable[e].value.forEach(el => {
                                if (Object.keys(el) && Object.keys(el).length > 0
                                    && (!el.firstTopUpAmount || !el.topUpCount)) {
                                    isValid = false;
                                    isReferralResult = false;
                                }
                            })
                        }

                        let levelParam = {
                            levelId: vm.allPlayerLvl[idx]._id,
                            value: vm.rewardMainParamTable[e].value
                        };

                        curReward.param.rewardParam.push(levelParam);
                    });



                    if (vm.showRewardTypeData && vm.showRewardTypeData.name && vm.showRewardTypeData.name == 'PlayerFestivalRewardGroup') {
                        // param table 2
                        Object.keys(vm.rewardMainParamTable2).forEach((e, idx) => {
                            curReward.param.rewardParam[idx].value = curReward.param.rewardParam[idx].value.concat(vm.rewardMainParamTable2[e].value)
                        });
                        // param table 3
                        Object.keys(vm.rewardMainParamTable3).forEach((e, idx) => {
                            curReward.param.rewardParam[idx].value = curReward.param.rewardParam[idx].value.concat(vm.rewardMainParamTable3[e].value)
                        });
                        // param table 4
                        Object.keys(vm.rewardMainParamTable4).forEach((e, idx) => {
                            curReward.param.rewardParam[idx].value = curReward.param.rewardParam[idx].value.concat(vm.rewardMainParamTable4[e].value)
                        });
                        // param table 5
                        Object.keys(vm.rewardMainParamTable5).forEach((e, idx) => {
                            curReward.param.rewardParam[idx].value = curReward.param.rewardParam[idx].value.concat(vm.rewardMainParamTable5[e].value)
                        });
                        // param table 6
                        Object.keys(vm.rewardMainParamTable6).forEach((e, idx) => {
                            curReward.param.rewardParam[idx].value = curReward.param.rewardParam[idx].value.concat(vm.rewardMainParamTable6[e].value)
                        });
                    }
                } else {

                }

                if (vm.showReward && vm.showReward.display) {
                    for (let i=0; i < vm.showReward.display.length; i++) {
                        if (vm.showReward.display[i].displayId == "" && vm.showReward.display[i].displayTitle == "" && vm.showReward.display[i].displayTextContent == "") {
                            vm.showReward.display.splice(i, 1);
                        }
                    }

                    curReward.display = vm.showReward.display || [];
                }

                if (vm.showRewardTypeData.isGrouped === true && vm.showRewardTypeData.name === 'BaccaratRewardGroup') {
                    if (vm.rewardParams && vm.rewardParams.rewardParam && vm.rewardParams.rewardParam.length > 0) {
                        vm.rewardParams.rewardParam.forEach(x => {
                            if (x && x.value && x.value.length > 0) {
                                for (let i = 0; i < x.value.length; i++) {
                                    if (!(x.value[i] && Object.keys(x.value[i]).length)) {
                                        isValid = false;
                                        break;
                                    }
                                    if (!(x.value[i].betType && (x.value[i].betType == "庄对" || x.value[i].betType == "闲对"))) {
                                        if (!x.value[i].hasOwnProperty("hostResult")) {
                                            isValid = false;
                                            isHostResult = false;
                                            break;
                                        }
                                        if (!x.value[i].hasOwnProperty("playerResult")) {
                                            isValid = false;
                                            isPlayerResult = false;
                                            break;
                                        }
                                    }
                                }
                            }
                        })
                    }
                }

                if (!isPeriodResultValid && !isApplyTypeValid) {
                    isValid = false;
                }

                if(curReward && curReward.condition){
                    curReward.condition = Object.assign(curReward.condition, vm.rewardVisible);
                }

                var sendData = {
                    query: {_id: vm.showReward._id},
                    updateData: curReward
                };

                console.log('editReward sendData', sendData);
                if (isValid) {
                    socketService.$socket($scope.AppSocket, 'updateRewardEvent', sendData, function (data) {
                        let rewardItem = data && data.data ? data.data: null;
                        if (vm.showRewardTypeData.name === 'PlayerFestivalRewardGroup') {
                            vm.rewardTabClicked(vm.reloadReward(rewardItem), vm.filterRewardPlatform);
                        } else {
                            vm.rewardTabClicked('', vm.filterRewardPlatform);
                        }
                        vm.afterEventCreated(data, vm.showReward);
                        vm.platformRewardPageName = 'showReward';
                        console.log('ok');

                    }, function (data) {
                        console.log("created not", data);
                        vm.rewardTabClicked('', vm.filterRewardPlatform);
                    });
                } else {
                    if (!isHostResult) {
                        socketService.showErrorMessage($translate("Banker Result is required"));
                    } else if (!isPlayerResult) {
                        socketService.showErrorMessage($translate("Player Result is required"));
                    } else if (!isPeriodResultValid && !isApplyTypeValid) {
                        socketService.showErrorMessage($translate('Reward interval does not valid for this reward apply type'));
                    } else if (!isReferralResult) {
                        socketService.showErrorMessage($translate('First Top Up Amount, Top Up Count is required more than zero'));
                    } else {
                        socketService.showErrorMessage($translate('Min Consumption Amount, Reward Amount is required'));
                    }
                    $scope.$evalAsync(() => {
                        vm.disableAllRewardInput(false);
                    });
                }
            }
